    constructor(service, router, activatedRoute) {
        this.service = service;
        this.router = router;
        this.activatedRoute = activatedRoute;
        this.employeeData$ = new Observable();
        this.id = this.activatedRoute.snapshot.params['id'];
        console.log(this.id);
        if (this.id) {
            this.employeeData$ = this.service.getEmployeeById(this.id);
        }
    }
    ngOnInit() {
        this.employeeDepartment$ = this.service.getEmployeeDepartemnt();
    }
    // Add employee
    addEmployee(employeeForm) {
        this.service.addEmployee(employeeForm).subscribe((res) => this.router.navigateByUrl('mvp/list'));
    }
    // Edit Employee
    editEmployee(employeeForm) {
        this.service.editEmployee(employeeForm, this.id).subscribe((res) => {
            this.router.navigateByUrl('mvp/list');
        });
    }
}
